package me.key.appmarket.network;public class ErrorMessage {	public static String getErrorMessage(int code) {		String msg = "Network unstable.";		msg = "您的网络不稳定";		switch (code) {		case 100:			msg = "未知错误";			break;		case 101:			msg = "错误接口";			break;		case 102:			msg = "用户未登录";			break;		case 103:			msg = "请求方式错误";			break;		case 104:			msg = "输入参数错误";			break;		case 309:			msg = "存储空间不足";			break;		case 1000:			msg = "网络错误";			break;		case 1002:			msg = "本地文件已删除";			break;		default:			msg = "本地文件已删除";			break;		}		return msg;	}}